import React, {createContext,useReducer} from 'react'
import AppReducer from './AppReducer'
const initialstate = {
    transactions: [
        {id:1, text:"food", amount:1000},
        {id:4, text:"flower", amount: -500}
    ]
}
export const GlobalContext = createContext(initialstate)

export const GlobalProvider = ({children}:any) =>{
const [state,dispatch] = useReducer(AppReducer,initialstate)

 const deletetransitions=(id:any)=>{
    dispatch({
        type: "remove",
        payload: id
    })
}
 const addtransactions = (id:any)=>{
    dispatch({
        type: "add",
        payload: id
    })
}
 const rem = (id:any) =>{
    dispatch({
        type: "sub",
        payload: id
    })
}
return (
<GlobalContext.Provider value={{transactions: state.transactions,rem,addtransactions,deletetransitions}}>{children}</GlobalContext.Provider>
)
}
